
<div class="mapwrapper col-xs-push-2 col-xs-10 no-padding">
    <div class=" col-xs-6 no-padding">
         <script src='https://maps.googleapis.com/maps/api/js?v=3.exp'></script><div style='overflow:hidden;height:500px;'><div id='gmap_canvas1' style='height:500px;'></div><div><small><a href="http://embedgooglemaps.com">                  embed google map              </a></small></div><div><small><a href="http://freedirectorysubmissionsites.com/">reedirectorysubmissionsites.com</a></small></div><style>#gmap_canvas img{max-width:none!important;background:none!important}</style></div><script type='text/javascript'>function init_map(){var myOptions = {zoom:13,center:new google.maps.LatLng(50.9214626,5.890487000000007),mapTypeId: google.maps.MapTypeId.ROADMAP};map = new google.maps.Map(document.getElementById('gmap_canvas1'), myOptions);marker = new google.maps.Marker({map: map,position: new google.maps.LatLng(50.9214626,5.890487000000007)});infowindow = new google.maps.InfoWindow({content:'<strong>Raamsdonk</strong><br>Nederland, Stationstraat 14<br>'});google.maps.event.addListener(marker, 'click', function(){infowindow.open(map,marker);});infowindow.open(map,marker);}google.maps.event.addDomListener(window, 'load', init_map);</script>
    </div>
